def _get_block_hashes(
    self,
    req: Request,
    start_idx: int = 0,
    end_idx: int | None = None,
) -> Iterable[BlockHash]:
    return islice(
        req.block_hashes,
        self.block_size_factor * start_idx + self.block_size_factor - 1,
        self.block_size_factor * end_idx if end_idx else None,
        self.block_size_factor,
    )

