def send_async(self):
    while True:
        with self.send_queue_cv:
            while not self.send_queue:
                self.send_queue_cv.wait()
            item = self.send_queue.popleft()
            if not self.send_queue:
                self.send_queue_cv.notify()
        self.send_sync(item)

