def ncclCommInitRank(
    self, world_size: int, unique_id: ncclUniqueId, rank: int
) -> ncclComm_t:
    comm = ncclComm_t()
    self.NCCL_CHECK(
        self._funcs["ncclCommInitRank"](
            ctypes.byref(comm), world_size, unique_id, rank
        )
    )
    return comm

