def unique_id_from_bytes(self, data: bytes) -> ncclUniqueId:
    if len(data) != 128:
        raise ValueError(
            f"Expected 128 bytes for ncclUniqueId, got {len(data)} bytes"
        )
    unique_id = ncclUniqueId()
    ctypes.memmove(ctypes.addressof(unique_id.internal), data, 128)
    return unique_id

