def all_constraints_none(self) -> bool:
    """
    Returns True if all structured-output constraint fields are None.
    """
    return all(
        getattr(self, field) is None
        for field in (
            "json",
            "regex",
            "choice",
            "grammar",
            "json_object",
            "structural_tag",
        )
    )

